use Illuminate\Database\Seeder;
use SSHAM\Role;
use SSHAM\User;

class UsersTableSeeder extends Seeder
{

    /**
     * Run the database seeds.
     *
     * @return void
     */
    public function run()
    {
        DB::table('users')->delete();

        $users = array(
            array(
                'username' => 'admin',
                'email' => 'admin@example.org',
                'auth_type' => 'local',
                'password' => bcrypt('admin'),
                'public_key' => 'ssh-rsa  admin@ssham',
                'fingerprint' => '49:97:ee:68:89:7e:cc:ce:60:e7:c9:c8:fe:1d:c0:d3',
                'enabled' => true,
                'role' => 'admin',
            ),
            array(
                'username' => 'user',
                'email' => 'user@example.org',
                'auth_type' => 'local',
                'password' => bcrypt('user'),
                'public_key' => 'ssh-rsa  user@ssham',
                'fingerprint' => '9d:5c:d0:95:09:e2:22:45:ca:0a:20:c9:13:05:63:1c',
                'enabled' => false,
                'role' => 'user',
            )
        );

        foreach ($users as $userData) {
            $user = User::create(array_except($userData, array('role')));
            $role = Role::where('name', $userData['role'])->get()->first();
            $user->attachRole($role);
            Log::info('Created user ' . $user->username);
        }
    }

}
